mapper.insert:
    insert into tbl_tran (id, owner, money, name, expected_date, customer_id, stage, type, source, activity_id,
                          contacts_id, create_by, create_time,  description, contact_summary,
                          next_contact_time)
    values (#{id},#{owner},#{money},#{name},#{expectedDate},#{customerId},#{stage},#{type},#{source},#{activityId},#{
contactsId},#{createBy},#{createTime},#{description},#{contactSummary},#{
nextContactTime});

mapper.select:
select a.id,b.name as owner,a.name,c.name as customer_id,d.value as stage,ifnull(e.value,'') as type,ifnull(f.value,'') as source,ifnull(g.fullname,'') as contacts_id from tbl_tran a join tbl_user b on b.id=a.owner join tbl_customer c on c.id=a.customer_id left join tbl_dic_value d on d.id=a.stage left join tbl_dic_value e on e.id=a.type left join tbl_dic_value f on f.id=a.source left join tbl_contacts g on g.id=a.contacts_id
and b.name like '%' #{owner} '%'
and c.name like '%' #{customerId} '%'
and d.value =#{stage}
and e.value=#{type}
and a.name like '%' #{name} '%'
and f.value =#{source}
and g.fullname like '%' #{contactsId} '%'
order by a.create_time desc limit #{beginNo} ,#{pageSize}

select count(*) from tbl_tran a join tbl_user b on b.id=a.owner join tbl_customer c on c.id=a.customer_id left join tbl_dic_value d on d.id=a.stage left join tbl_dic_value e on e.id=a.type left join tbl_dic_value f on f.id=a.source join tbl_contacts g on g.id=a.contacts_id
and b.name like '%' #{owner} '%'
and c.name like '%' #{customerId} '%'
and d.value =#{stage}
and e.value=#{type}
and a.name like '%' #{name}'%'
and f.value =#{source}
and g.fullname like '%' #{contactsId} '%'

mapper.update:
update tbl_tran set stage = #{stage,jdbcType=VARCHAR}, type = #{type,jdbcType=VARCHAR}, source = #{source,jdbcType=VARCHAR},
edit_by = #{editBy,jdbcType=VARCHAR}, edit_time = #{editTime,jdbcType=CHAR}
where id = #{id,jdbcType=CHAR}

mapper.select:
select a.id,a.owner,a.money,a.name,a.expected_date,a.customer_id,a.stage,a.type,a.source,a.activity_id,a.
  contacts_id,a.create_by,a.create_time,a.edit_by,a.edit_time,a.description,a.contact_summary,a.next_contact_time,b.fullname as contactName
from tbl_tran a left join tbl_contacts b on b.id=a.contacts_id where a.id=#{id};
